import React, { useState, useEffect } from 'react'
import './Receitas.css'
import Axios from 'axios';
import { NavLink, Redirect } from 'react-router-dom'
// Importa o withRouter do pacote react-router
import { withRouter } from 'react-router'

const Receitas = props => {
  const [receitas, setReceitas] = useState([]);

  const gotoReceita = id => {
    Axios.get('/api/receita/' + id)
      .then(res => {
        console.log('aaa')
        setReceitas(res.data);
        // props.history.push({
        //   pathname: '/show-receita',
        //   state: { receita: res.data }
        // })
      })
      .catch(err => {
        console.log(err)
      })
    // Essa função aqui em cima não tá funcionando?
    // Essa rota receitas tá contida no BrowserRouter? -> ta la no app
    // acho que nao ta pegando pq nao tem history pra dar push
  }
  useEffect(() => {
    // O que precisa fazer aqui dentro? ir pra aquela rota n precisa de if né?
    // Aqui dentro do useEffect vc vai checar se a lista de receitas tá
    // Receitas é uma array?
    if (!!receitas.length) {
      props.history.push({
        pathname: "/show-receitas",
        state: { receitas }
      })
    }
  }, [receitas]);

  return (
    <div className="card m-2 ml-auto card-largo card-receita">
      <img src="/" alt="" className="card-img-top" />
      <div className="card-body">
        <h5 className="card-title">{props.nome}</h5>
        <ul>
          {props.ing.map(ing => <li key={ing}>{ing}</li>)}
        </ul>
        <button onClick={() => gotoReceita(props.id)} className="btn btn-primary">Go!</button>
      </div>
    </div>
  )
}

export default withRouter(Receitas)
